require 'securerandom'

crypt_var = Array.new(130,"1")
crypt_int = crypt_var.map{"#{SecureRandom.random_number(..)}"}
File.open("assets/keys/crypt.gkey", "w+") do |reset|
  reset.write "module Crypt\n\n	@@cypher_bak = #{crypt_int}\n\nend\n"
end

puts "Cypher keys have been reset.\n\nRemember to keep a backup copy!\n\nPress ENTER to exit the console."

gets